#include "../common.h"

class Solution {
public:
    int numDecodings(string s) {

        uint64_t decoding[s.length()+1];
        memset(decoding, 0, sizeof(decoding));

        if(s.length() == 0 || s[0]=='0')
            return 0;
        if(s.length() < 2)
            return 1;

        if(s[0]>'0')
            decoding[0] = 1;

        int tmp = (s[0] - '0')*10 + s[1] -'0';
        if(s[0] != '0' && tmp <= 26)
            decoding[1] = 1;
        if(s[1]!='0')
            decoding[1] += decoding[0];


        for(int i=2;i<s.length();i++){
            if(s[i] != '0')
                decoding[i] = decoding[i-1];

            int tmp=(s[i-1] - '0') * 10 + s[i] - '0';
            if(s[i-1] != '0' && tmp <= 26)
                decoding[i] += decoding[i-2];
        }


        return decoding[s.length()-1];
    }
};

int main(){
    
    string str="";

    Solution s;
    cout << s.numDecodings(str) << endl;
    

}
